import { Link } from "react-router-dom";
import { useState } from "react";
import CatwayList from '../catwayList';
const [newNumber, setNewNumber] = useState("");
const [newType, setNewType] = useState("Short");
const [newState, setNewState] = useState("");
const [catwayId, setCatwayId] = useState("");

const Catways = () => {
  return (
    <main className="container">
      <div className="text-center">
        <h1>Catways</h1>
      </div>
      <hr />
        <div>
            <h2>Vous pouvez gérer les catways ici</h2>
            <div className="mb-4">
                <h3>Liste des catways</h3>
                <button type="button" className="btn btn-primary " data-bs-toggle="modal" data-bs-target="#ModalList">Afficher</button>
            </div>

            {/*Modale*/}
            <div className="modal fade" id="ModalList" tabindex="-1" aria-hidden="true"> 
                <div className="modal-dialog modal-dialog-centered">
                    <div className="modal-content bg-dark text-white">
                        <div className="modal-header">
                            <h3 className="modal-title" id="modalLabel">Liste des catways</h3>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <div className="row align-items-center">
                               <CatwayList/>
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Fermer</button>
                        </div>
                    </div>
                </div>
            </div>

            <h3>Rechercher un catway pour des détails ou pour sa suppression</h3>
            <div className="d-flex gap-2 mb-4">
                <input type="number" className="form-control" placeholder="Numéro du catway" value={catwayId} onChange={(e) => setCatwayId(e.target.value)}/>
                <Link className="btn btn-primary" to={`/catways/${catwayId}`} disabled={!catwayId} >Détails</Link>
                <Link className="btn btn-danger" to={`/catways/${catwayId}/delete`} disabled={!catwayId} >Supprimer</Link>
            </div>
            
            <h3>Modifier un catway</h3>
            <form>
                <div className="mb-3">
                    <label htmlFor="InputNumber" className="form-label">Numero du catway</label>
                    <input type="number" className="form-control" id="catwayNumber"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="InputState" className="form-label">Nouvel état</label>
                    <input type="text" className="form-control" id="catwayState"/>
                </div>
                <button type="submit" className="btn btn-primary">Modifier</button>
            </form>   
            <h3>Ajouter un catway</h3>
            <form onSubmit={addCatway}>
                <div className="row mb-3">
                    <label className="col-sm-2 col-form-label">Numéro du nouveau catway</label>
                    <div className="col-sm-10">
                        <input type="number" className="form-control" value={newNumber} onChange={(e) => setNewNumber(e.target.value)}/>
                    </div>
                </div>
                <fieldset className="row mb-3">
                    <legend className="col-form-label col-sm-2 pt-0">Type</legend>
                    <div className="col-sm-10">
                        <div className="form-check">
                            <input className="form-check-input" type="radio" name="type" value="Long" checked={newType === "Long"} onChange={(e) => setNewType(e.target.value)}/>
                            <label className="form-check-label">Long</label>
                            </div>
                            <div class="form-check">
                            <input className="form-check-input" type="radio" name="type" value="Short" checked={newType === "Short"} onChange={(e) => setNewType(e.target.value)}/>
                            <label class="form-check-label" for="ShortType">Short</label>
                        </div>
                    </div>
                </fieldset>
                <div className="row mb-3">
                    <label htmlFor="InputState" className="col-sm-2 col-form-label">Etat du catway</label>
                    <div class="col-sm-10">
                        <input type="text" className="form-control" id="catwayState"/>
                    </div>
                </div>
                <div className="col-12">
                    <button type="submit" className="btn btn-primary">Ajouter</button>
                </div>
            </form>  
        </div>      
    </main>
  );
};

export default Catways;

const handleAddCatway = async (e) => {
    e.preventDefault(); // empêche le rechargement de la page

    const newCatway = {
        catwayNumber: newNumber,
        catwayType: newType,
        catwayState: newState
    };

    try {
        const response = await fetch("/catways/add", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(newCatway)
        });

        if (response.ok) {
            alert("Catway ajouté !");
            setNewNumber("");
            setNewType("");
            setNewState("");
        } else {
            alert("Erreur lors de l'ajout");
        }
    } catch (error) {
        console.error(error);
        alert("Erreur serveur");
    }
};
